//${SMs::Calc::eval} .........................................................
static bool Calc_eval(Calc * const me,
    float64_t op,
    uint8_t oper)
{
    float64_t result;
    if ((oper == KEY_NULL) || (oper == KEY_PLUS) || (oper == KEY_MINUS)) {
        switch (me->oper2) {
            case KEY_MULT: {
                me->op2 *= op;
                break;
            }
            case KEY_DIVIDE: {
                if ((-1e-30 < op) && (op < 1e-30)) {
                    BSP_display_error(" Error 0 "); // divide by zero
                    return false;
                }
                me->op2 /= op;
                break;
             }
             default: { // no op2 yet
                me->op2 = op;
                me->oper2 = oper;
                break;
             }
        }
        switch (me->oper1) {
            case KEY_PLUS: {
                me->op1 += me->op2;
                break;
            }
            case KEY_MINUS: {
                me->op1 -= me->op2;
                break;
            }
            case KEY_MULT: {
                me->op1 *= me->op2;
                break;
            }
            case KEY_DIVIDE: {
                if ((-1e-30 < me->op2) && (me->op2 < 1e-30)) {
                    BSP_display_error(" Error 0 "); // divide by zero
                    return false;
                }
                me->op1 /= me->op2;
                break;
            }
            default: {
                Q_ERROR();
                break;
            }
        }
        me->oper1 = oper;
        me->oper2 = KEY_NULL;
        result = me->op1;
    }
    else { // (oper == KEY_MULT) || (oper == KEY_DIV)
        switch (me->oper2) {
            case KEY_MULT: {
                me->op2 *= op;
                break;
            }
            case KEY_DIVIDE: {
                if ((-1e-30 < op) && (op < 1e-30)) {
                    BSP_display_error(" Error 0 "); // divide by zero
                    return false;
                }
                me->op2 /= op;
                break;
            }
            default: { // oper2 not provided yet
                me->op2 = op;
                break;
            }
        }
        me->oper2 = oper;
        result = me->op2;
    }

    if ((result < -99999999.0) || (99999999.0 < result)) {
        BSP_display_error(" Error 1 "); // out of range
        return false;
    }
    if ((-0.0000001 < result) && (result < 0.0000001)) {
        result = 0.0;
    }
    BSP_display(result);

    return true;
}

